import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import App from './App';

const root = ReactDOM.createRoot(document.getElementById('root'));

// Перед отрисовкой компонента App, мы покажем наш loader
root.render(
    <div id="loader"></div>,
    document.body.appendChild(document.createElement('div'))
);

// После того, как все компоненты App отрисовались, мы скроем loader
setTimeout(() => {
    root.render(
        <React.StrictMode>
            <App />
        </React.StrictMode>
    );

    document.getElementById('loader').style.animation = 'showLoader .5s forwards';
}, 5000) // Установите желаемое время задержки здесь в миллисекундах
