import React, { Component } from 'react';
import s from './Categories.module.css';
import axios from 'axios';

class Categories extends Component {
    constructor(props) {
        super(props);
        this.state = {
            categories: [],
            price: null
        };
    }

    componentDidMount() {
        axios
            .get('https://fakestoreapi.com/products/categories')
            .then((res) => {
                const categories = res.data.map((category) => ({
                    key: category,
                    name: category.charAt(0).toUpperCase() + category.slice(1),
                }));
                categories.unshift({ key: 'all', name: 'Всё' });
                this.setState({ categories });
            })
            .catch((error) => {
                console.log(error);
            });
    }

    sortAscending = () => {
        axios
            .get('https://fakestoreapi.com/products?sort=asc')
            .then((res) => {
                this.props.updateProducts(res.data);
            })
            .catch((error) => {
                console.log(error);
            });
    };

    sortDescending = () => {
        axios
            .get('https://fakestoreapi.com/products?sort=desc')
            .then((res) => {
                this.props.updateProducts(res.data);
            })
            .catch((error) => {
                console.log(error);
            });
    };

    render() {
        return (
            <div className={s.categories}>
                <div className={s.sortButtons}>
                    <button onClick={this.sortAscending}>Сортировать по возрастанию цены</button>
                    <button onClick={this.sortDescending}>Сортировать по убыванию цены</button>
                </div>
                {this.state.categories.map((el) => (
                    <div key={el.key} onClick={() => this.props.chooseCategory(el.key)}>
                        {el.name}
                    </div>
                ))}
            </div>
        );
    }
}

export default Categories;
