import React, { useState, useEffect } from 'react';
import axios from 'axios';
import s from './Header.module.css';
import { FaShoppingBasket } from 'react-icons/fa';
import Order from './Order/Order';

const showOrders = (props) => {
    let summa = 0;
    props.orders.forEach((el) => (summa += Number.parseFloat(el.price)));
    return (
        <div>
            {props.orders.map((el) => (
                <Order onDelete={props.onDelete} key={el.id} item={el} />
            ))}
            <p className={s.summa}>Сумма: {new Intl.NumberFormat().format(summa)}$</p>
        </div>
    );
};

const showNothing = () => {
    return (
        <div className={s.empty}>
            <h2>Товаров нет</h2>
        </div>
    );
};

const Header = (props) => {
    let [cartOpen, setCartOpen] = useState(false);
    let [searchQuery, setSearchQuery] = useState('');
    let [searchResults, setSearchResults] = useState([]);

    useEffect(() => {
        if (searchQuery) {
            axios
                .get(`https://fakestoreapi.com/products?title=${searchQuery}`)
                .then((res) => {
                    setSearchResults(res.data);
                })
                .catch((error) => {
                    console.log(error);
                });
        }
    }, [searchQuery]);

    return (
        <div className={s.container}>
            <div className={s.navigation}>
                <span className={s.logo}>Logo</span>
                <div className={s.menu}>
                    <FaShoppingBasket
                        onClick={() => setCartOpen((cartOpen = !cartOpen))}
                        className={cartOpen ? s.cartButtonActive : s.cartButton}
                    />
                    {cartOpen && (
                        <div className={s.shopCart}>
                            {props.orders.length > 0 ? showOrders(props) : showNothing()}
                        </div>
                    )}
                    <input
                        type="text"
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                        placeholder="Поиск по продуктам"
                        className={s.searchInput}
                    />
                    {searchResults.length > 0 && (
                        <div className={s.searchResults}>
                            {searchResults.map((item) => (
                                <div key={item.id} onClick={() => props.onAdd(item)}>
                                    {item.title}
                                </div>
                            ))}
                        </div>
                    )}
                </div>
            </div>
            <div className={s.presentation}></div>
        </div>
    );
};

export default Header;
