import React, { Component } from 'react';
import s from './Categories.module.css';
import axios from 'axios';

class Categories extends Component {
    constructor(props) {
        super(props);
        this.state = {
            categories: [],
            products: [],
        };
    }

    componentDidMount() {
        axios
            .get('https://fakestoreapi.com/products/categories')
            .then((res) => {
                const categories = res.data.map((category) => ({
                    key: category,
                    name: category.charAt(0).toUpperCase() + category.slice(1),
                }));
                categories.unshift({ key: 'all', name: 'Всё' });
                this.setState({ categories });
            })
            .catch((error) => {
                console.log(error);
            });

        axios
            .get('https://fakestoreapi.com/products')
            .then((res) => {
                const products = res.data;
                this.setState({ products });
            })
            .catch((error) => {
                console.log(error);
            });
    }

    sortProductsByPrice = (order) => {
        const sortedProducts = this.state.products.sort((a, b) => {
            if (order === 'asc') {
                return a.price - b.price;
            } else {
                return b.price - a.price;
            }
        });
        this.setState({ products: sortedProducts });
    };

    render() {
        return (
            <div className={s.categories}>
                <div className={s.category}>
                    <span>Категории:</span>
                    {this.state.categories.map((el) => (
                        <div
                            key={el.key}
                            onClick={() => this.props.chooseCategory(el.key)}
                        >
                            {el.name}
                        </div>
                    ))}
                </div>
                <div className={s.sort}>
                    <span>Сортировка:</span>
                    <button onClick={() => this.sortProductsByPrice('asc')}>
                        По возрастанию цены
                    </button>
                    <button onClick={() => this.sortProductsByPrice('desc')}>
                        По убыванию цены
                    </button>
                </div>
            </div>
        );
    }
}

export default Categories;
