import React, { useState } from 'react';
import s from './Header.module.css';
import { FaShoppingBasket } from 'react-icons/fa';
import Order from './Order/Order';

const showOrders = (props) => {
    let summa = 0;
    props.orders.forEach((el) => (summa += Number.parseFloat(el.price)));
    return (
        <div>
            {props.orders.map((el) => (
                <Order onDelete={props.onDelete} key={el.id} item={el} />
            ))}
            <p className={s.summa}>Сумма: {new Intl.NumberFormat().format(summa)}$</p>
        </div>
    );
};

const showNothing = () => {
    return (
        <div className={s.empty}>
            <h2>Товаров нет</h2>
        </div>
    );
};

const Header = (props) => {
    const [cartOpen, setCartOpen] = useState(false);
    const [searchValue, setSearchValue] = useState('');

    const handleSearchChange = async (event) => {
        setSearchValue(event.target.value);
        const response = await fetch('https://fakestoreapi.com/products');
        const data = await response.json();
        const filteredItems = data.filter((item) =>
            item.title.toLowerCase().includes(event.target.value.toLowerCase())
        );
        props.onSearch(filteredItems);
    };


    return (
        <div className={s.container}>
            <div className={s.navigation}>
                <span className={s.logo}>Logo</span>
                <div className={s.menu}>
                    <input
                        type="text"
                        placeholder="Поиск..."
                        value={searchValue}
                        onChange={handleSearchChange}
                    />
                    <FaShoppingBasket
                        onClick={() => setCartOpen((cartOpen) => !cartOpen)}
                        className={cartOpen ? s.cartButtonActive : s.cartButton}
                    />
                    {cartOpen && (
                        <div className={s.shopCart}>
                            {props.orders.length > 0 ? showOrders(props) : showNothing()}
                        </div>
                    )}
                </div>
            </div>
            <div className={s.presentation}></div>
        </div>
    );
};

export default Header;
