import React, { Component } from 'react'
import s from './Categories.module.css'
import axios from "axios";

class Categories extends Component {
    constructor(props) {
        super(props)
        this.state = {
            categories: []
        }
    }

    componentDidMount() {
        axios.get('https://fakestoreapi.com/products/categories')
            .then(res => {
                const categories = res.data.map(category => ({key: category, name: category.charAt(0).toUpperCase() + category.slice(1)}))
                categories.unshift({key: 'all', name: 'Всё'})
                this.setState({categories})
            })
            .catch(error => {
                console.log(error)
            })
        axios.get('https://fakestoreapi.com/products?sort=desc')
            .then(res => {
                this.setState({ products: res.data })
            })
            .catch(error => {
                console.log(error)
            })
    }


    render() {
        // Фильтруем товары в соответствии с выбранной категорией
        const products = this.props.category === 'all' ?
            this.state.products :
            this.state.products.filter(product => product.category === this.props.category)

        // Сортируем товары по цене
        const sortedProducts = [...products].sort((a, b) => b.price - a.price)

        return (
            <div className={s.categories}>
                {this.state.categories.map(el => (
                    <div key={el.key} onClick={() => this.props.chooseCategory(el.key)}>{el.name}</div>
                ))}
                <div>
                    <h2>Список товаров</h2>
                    <ul>
                        {sortedProducts.map(product => (
                            <li key={product.id}>
                                <h3>{product.title}</h3>
                                <p>Цена: {product.price}</p>
                            </li>
                        ))}
                    </ul>
                </div>
            </div>
        )
    }
}

export default Categories
