import React, { Component } from 'react'
import s from './Categories.module.css'
import axios from "axios";

class Categories extends Component {
    constructor(props) {
        super(props)
        this.state = {
            categories: [],
        }
        this.sortByPriceAsc = this.sortByPriceAsc.bind(this);
        this.sortByPriceDesc = this.sortByPriceDesc.bind(this);
    }

    componentDidMount() {
        axios.get('https://fakestoreapi.com/products/categories')
            .then(res => {
                const categories = res.data.map(category => ({key: category, name: category.charAt(0).toUpperCase() + category.slice(1)}))
                categories.unshift({key: 'all', name: 'All'})
                this.setState({categories})
            })
            .catch(error => {
                console.log(error)
            })
    }


    sortByPriceAsc() {
        const currentItemsCopy = [...this.props.items];
        currentItemsCopy.sort((a, b) => a.price - b.price);
        this.props.onSortItems(currentItemsCopy);
    }

    sortByPriceDesc() {
        const currentItemsCopy = [...this.props.items];
        currentItemsCopy.sort((a, b) => b.price - a.price);
        this.props.onSortItems(currentItemsCopy);
    }

    render() {
        return (
            <div className={s.categories}>
                {this.state.categories.map(el => (
                    <div className={s.categories_items} key={el.key} onClick={() => this.props.chooseCategory(el.key)}>{el.name}</div>
                ))}
            </div>
        )
    }
}

export default Categories
