import React, { Component } from 'react'
import s from './Categories.module.css'
import axios from "axios";

class Categories extends Component {
    constructor(props) {
        super(props)
        this.state = {
            categories: [],
            sortDirection: 'asc'
        }
    }

    componentDidMount() {
        axios.get('https://fakestoreapi.com/products/categories')
            .then(res => {
                const categories = res.data.map(category => ({key: category, name: category.charAt(0).toUpperCase() + category.slice(1)}))
                categories.unshift({key: 'all', name: 'Всё'})
                this.setState({categories})
            })
            .catch(error => {
                console.log(error)
            })
    }

    sortByPrice = (direction) => {
        const products = [...this.props.products]
        if (direction === 'asc') {
            products.sort((a, b) => a.price - b.price)
        } else {
            products.sort((a, b) => b.price - a.price)
        }
        this.props.setProducts(products)
        this.setState({sortDirection: direction})
    }

    render() {
        return (
            <div className={s.categories}>
                {this.state.categories.map(el => (
                    <div key={el.key} onClick={() => this.props.chooseCategory(el.key)}>{el.name}</div>
                ))}
                <div className={s.sort}>
                    <div>Сортировать по цене:</div>
                    <div>
                        <button
                            className={this.state.sortDirection === 'asc' ? s.active : ''}
                            onClick={() => this.sortByPrice('asc')}>
                            От дешевых к дорогим
                        </button>
                        <button
                            className={this.state.sortDirection === 'desc' ? s.active : ''}
                            onClick={() => this.sortByPrice('desc')}>
                            От дорогих к дешевым
                        </button>
                    </div>
                </div>
            </div>
        )
    }
}

export default Categories
