import React, { Component } from 'react'
import s from './Order.module.css'
import {FaTrash} from 'react-icons/fa'

class Order extends Component {
    constructor(props) {
        super(props);
        this.state = {
            count: 1
        };
    }

    incrementCount = () => {
        const newCount = this.state.count + 1;
        const newSumma = this.props.item.price * newCount;
        this.setState({
            count: newCount,
            summa: newSumma
        });
        this.props.updateTotalSumma(newSumma);
    }

    decrementCount = () => {
        if (this.state.count > 0) {
            const newCount = this.state.count - 1;
            const newSumma = this.props.item.price * newCount;
            this.setState({
                count: newCount,
                summa: newSumma
            });
            this.props.updateTotalSumma(-newSumma);
        }
    }



    handleDelete = () => {
        this.props.onDelete(this.props.item.id);
    }

    render() {
        return (
            <div className={s.item}>
                <img src={this.props.item.image} alt="icon" />
                <h2>{this.props.item.title}</h2>
                <b>{this.props.item.price}$</b>
                <div className={s.counter}>
                    <button onClick={this.decrementCount}>-</button>
                    <span>{this.state.count}</span>
                    <button onClick={this.incrementCount}>+</button>
                </div>
                <FaTrash className={s.trash} onClick={this.handleDelete} />
                <p className={s.summa}>Сумма: {new Intl.NumberFormat().format(this.state.summa)}$</p>
            </div>
        )
    }
}

export default Order
