import s from './App.module.css';
import Header from './components/Header/Header';
import Main from './components/Main/Main';
import Footer from './components/Footer/Footer';
import React from 'react';
import Categories from './components/Main/Categories/Categories';
import ShowItem from './components/Main/ShowItem/ShowItem';
import axios from "axios";
import Item from "./components/Main/Item/Item";

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            orders: [],
            currentItems: [],
            items: null,
            showItem: false,
            fullItem: {},
            searchQuery: ''
        };
        this.addToOrder = this.addToOrder.bind(this);
        this.deleteOrder = this.deleteOrder.bind(this);
        this.chooseCategory = this.chooseCategory.bind(this);
        this.onShowItem = this.onShowItem.bind(this);
    }

    componentDidMount() {
        axios.get('https://fakestoreapi.com/products')
            .then(res => {
                const items = res.data;
                this.setState({ items, currentItems: items });
            })
            .catch(function (error){
                console.log(error);
            });
    }

    render() {
        return (
            <div className={s.wrapper}>
                <Header orders={this.state.orders} onDelete={this.deleteOrder} onSearch={this.searchItems} />
                <Categories chooseCategory={this.chooseCategory}/>
                <Main onShowItem={this.onShowItem} items={this.state.currentItems} onAdd={this.addToOrder}/>
                {this.state.currentItems.map((item) => (
                    <Item key={item.id} item={item} onShowItem={this.onShowItem} onAdd={this.addToOrder} />
                ))}
                <Footer/>
            </div>
        );
    }

    onShowItem(item) {
        this.setState({fullItem: item});
        this.setState({showItem: !this.state.showItem});
        this.setState({ fullItem: item, showItem: true });
    }

    chooseCategory(category) {
        if(category === 'all'){
            this.setState({currentItems: this.state.items});
            return;
        }
        this.setState({
            currentItems: this.state.items.filter(el => el.category === category)
        });
    }

    deleteOrder(id) {
        this.setState({orders: this.state.orders.filter(el => el.id !== id)});
    }

    addToOrder(item) {
        let isInArray = false;
        this.state.orders.forEach(el => {
            if (el.id === item.id)
                isInArray = true;
        });
        if (!isInArray)
            this.setState({orders: [...this.state.orders, item]});
    }

    handleSearchInputChange = event => {
        this.setState({ searchQuery: event.target.value });
    }

    handleSearchInputKeyPress = event => {
        if (event.key === 'Enter') {
            this.props.onSearch(this.state.searchQuery);
        }
    }

    searchItems(query) {
        const currentItems = this.state.items.filter(item =>
            item.title.toLowerCase().includes(query.toLowerCase())
        );
        this.setState({ currentItems });
    }
}

export default App;
