import React, { Component } from 'react'
import s from './Categories.module.css'
import axios from "axios";

class Categories extends Component {
    constructor(props) {
        super(props)
        this.state = {
            categories: [],
            price: [],
            sortOrder: 'asc'
        }
    }

    componentDidMount() {
        axios.get('https://fakestoreapi.com/products/categories')
            .then(res => {
                const categories = res.data.map(category => ({key: category, name: category.charAt(0).toUpperCase() + category.slice(1)}))
                categories.unshift({key: 'all', name: 'All'})
                this.setState({categories})
            })
            .catch(error => {
                console.log(error)
            })
        axios.get('https://fakestoreapi.com/products?sort=desc')
            .then(res => {
                const price = res.data.map(price => ({key: price.id, name: price.price}))
                if (this.state.sortOrder === 'asc') {
                    price.sort((a, b) => a.name - b.name);
                } else {
                    price.sort((a, b) => b.name - a.name);
                }
                this.setState({price})
            })
            .catch(error => {
                console.log(error)
            })
    }



    render() {
        return (
            <div className={s.categories}>
                {this.state.categories.map(el => (
                    <div className={s.categories_items} key={el.key} onClick={() => this.props.chooseCategory(el.key)}>{el.name}</div>
                ))}
                <div className={s.sort} onClick={() => {
                    const newSortOrder = this.state.sortOrder === 'asc' ? 'desc' : 'asc';
                    this.setState({sortOrder: newSortOrder}, () => {
                        this.componentDidMount();
                    });
                }}>Sort {this.state.sortOrder === 'asc' ? '↑' : '↓'}</div>
            </div>
        )
    }
}

export default Categories
