import s from './App.module.css'
import Header from './components/Header/Header'
import Main from './components/Main/Main'
import Footer from './components/Footer/Footer'
import React from 'react'
import white_room from '../src/assets/img/white_room.jpg'

class App extends React.Component {
    constructor(props) {
        super(props)
        this.state ={
            orders: [],
            items: [
                {
                    id: 1,
                    title: 'Серый стул',
                    img: white_room,
                    desc: 'Lorem ipsum dolor sit amet',
                    category: 'chairs',
                    price:'49.99'
                },
                {
                    id: 2,
                    title: 'Красный стол',
                    img: white_room,
                    desc: 'Lorem ipsum dolor sit amet',
                    category: 'tables',
                    price:'29.99'
                },
                {
                    id: 3,
                    title: 'Пиковый стул',
                    img: white_room,
                    desc: 'Lorem ipsum dolor sit amet',
                    category: 'chairs',
                    price:'149.99'
                },
                {
                    id: 4,
                    title: 'Серый стул',
                    img: white_room,
                    desc: 'Lorem ipsum dolor sit amet',
                    category: 'chairs',
                    price:'49.99'
                },
                {
                    id: 5,
                    title: 'Красный стол',
                    img: white_room,
                    desc: 'Lorem ipsum dolor sit amet',
                    category: 'tables',
                    price:'29.99'
                },
                {
                    id: 6,
                    title: 'Пиковый стул',
                    img: white_room,
                    desc: 'Lorem ipsum dolor sit amet',
                    category: 'chairs',
                    price:'149.99'
                },
                {
                    id: 7,
                    title: 'Серый стул',
                    img: white_room,
                    desc: 'Lorem ipsum dolor sit amet',
                    category: 'chairs',
                    price:'49.99'
                },
                {
                    id: 8,
                    title: 'Красный стол',
                    img: white_room,
                    desc: 'Lorem ipsum dolor sit amet',
                    category: 'tables',
                    price:'29.99'
                },
                {
                    id: 9,
                    title: 'Пиковый стул',
                    img: white_room,
                    desc: 'Lorem ipsum dolor sit amet',
                    category: 'chairs',
                    price:'149.99'
                },
            ]
        }
        this.addToOrder = this.addToOrder.bind(this)
    }
    render() {
        return (
            <div className={s.wrapper}>
                <Header orders={this.state.orders}/>
                <Main items={this.state.items} onAdd={this.addToOrder}/>
                <Footer/>
            </div>
        );
    }

    addToOrder(item){
        let isInArray = false
        this.state.orders.forEach(el => {
            if(el.id === item.id)
                isInArray = true
        })

        this.setState({orders:[...this.state.orders, item]})
    }
}

export default App;
